mapper.insert:
insert into message (req_id, content, create_datetime
  )
values (#{reqId,jdbcType=BIGINT}, #{content,jdbcType=VARCHAR}, #{createDatetime,jdbcType=TIMESTAMP}
  )

mapper.update:
update message
set content = #{content,jdbcType=VARCHAR},
  create_datetime = #{createDatetime,jdbcType=TIMESTAMP}
where req_id = #{reqId,jdbcType=BIGINT}

mapper.select:
select req_id, content, create_datetime
from message
where req_id = #{reqId,jdbcType=BIGINT}

